<?php if (! defined('BASEPATH')) exit('No direct script access allowed');/*	Get Info contains :	about, privacy, TOS (term and condition), and info*/class Info extends CI_Controller{	public function __construct()    {		parent::__construct();		$this->load->model('mod_contest','C');	}		public function index()    {		$this->about();	}	public function about()    {		$data['meta_title'] = "Sharetagram - Instagram Web Viewer"; 		$data['meta_description'] = "ABOUT | Use Instagram online! Sharetagram is the Best Instagram Web Viewer!";		$data['meta_keywords'] = "Instagram, IG, web, viewer, stats, photo, video, Facebook";		$q = $this->C->getInfo('about')->row();		$data['title'] = $q->info_name; 		$data['desc'] = $q->info_content; 		$data['content'] = 'info/index';		$this->load->view('layout/dashboard_view',$data);	}		public function privacy()    {		$data['meta_title'] = "Sharetagram - Instagram Web Viewer"; 		$data['meta_description'] = "PRIVACY POLICY |Use Instagram online! Sharetagram is the Best Instagram Web Viewer!";		$data['meta_keywords'] = "privacy,policy,Sharetagram,Share,Instagram,IG,PC,Web,Viewer, Facebook";		$q = $this->C->getInfo('privacy')->row();		$data['title'] = $q->info_name; 		$data['desc'] = $q->info_content; 		$data['content'] = 'info/index';		$this->load->view('layout/dashboard_view',$data);	}		public function tos()    {		$data['meta_title'] = "Terms Of Services - Sharetagram"; 		$data['meta_description'] = "Terms Of Services |Use Instagram online! Sharetagram is the Best Instagram Web Viewer!";		$data['meta_keywords'] = "terms,service,Sharetagram,Share,Instagram,IG,PC,Web,Viewer, Facebook";		$q = $this->C->getInfo('terms')->row();		$data['title'] = $q->info_name; 		$data['desc'] = $q->info_content; 		$data['content'] = 'info/index';		$this->load->view('layout/dashboard_view',$data);	}		public function contact()    {		$data['meta_title'] = "Contact Us - Sharetagram Instagram web viewer online"; 		$data['meta_description'] = "CONTACT | Use Instagram online! Sharetagram is the Best Instagram Web Viewer!";		$data['meta_keywords'] = "Instagram, IG, web, viewer, stats, photo, video, Facebook";		$q = $this->C->getInfo('contact')->row();		$data['title'] = $q->info_name; 		$data['desc'] = $q->info_content; 		$data['content'] = 'info/contact';		$this->load->view('layout/dashboard_view',$data);	}			public function contact_post()    {		$post = $this->input->post();		//pr($post);		$data_ins = array(			'name' => $post['name'],			'email' => $post['email'],			//'type' => $post['type'],			'message' => $post['message'],			'created_time' => date('Y-m-d H:i:s')		);		$q = $this->db->insert('contact',$data_ins);		if($q){			echo "Thanks, we will response your message immadiately";					}else{			echo "fail";		}		}		}