<script>    $("#view-like").click(function(){        $( this ).html(loading);        $.get('<?php echo base_url()."site/get_user_likes?mid=$media_id"?>',function(data,status){            $("#view-like").hide();            $(".likes ul").html(data);        });    });    $("#click_like").click(function(){        var fClass  = $( this ).data('act');        if(userSelfId==''){            alert("You should login");        }        else{            // Like item            if( fClass === 'like-icon' ){                $(this).removeClass('like-icon').addClass('like-icon-active');                var likeCount = <?php echo (int)$likes_count; ?>;                $("#like-count").text(likeCount+1);                var userSelfName = '<?php echo $user_self_name ?>';                //$(".likes ul").append('<li><a href="'+BASE+'user/'+userSelfId+'/'+userSelfName+'">'+userSelfName+'</a></li>');                $(".likes li:eq(0)").before('<li id="'+userSelfId+'" ><a href="'+BASE+'user/'+userSelfId+'/'+userSelfName+'">'+userSelfName+'</a></li>');                $( this ).data('act','like-icon-active');                var url = BASE+"site/post_like";                $.post(url,                    {                        mid : '<?php echo $media_id; ?>'                    },                    function(data,status){                        console.log(data);                        if(status==false){                            alert("failed. Please try again!");                            $(this).removeClass('like-icon-active').addClass('like-icon');                            $("#like-count").text(likeCount-1);                            $( this ).data('act','like-icon');                        }                        else{                            return true;                        }                    });            }            // UnLike item            else{                $(this).removeClass('like-icon-active').addClass('like-icon');                var likeCount = <?php echo (int)$likes_count; ?>;                $("#like-count").text(likeCount-1);                //var userSelfName = '<?php echo $user_self_name ?>';                //$(".likes ul").append('<li><a href="'+BASE+'user/'+userSelfId+'/'+userSelfName+'">'+userSelfName+'</a></li>');                //$(".likes li:eq(0)").before('<li><a href="'+BASE+'user/'+userSelfId+'/'+userSelfName+'">'+userSelfName+'</a></li>');                //$(li                //console.log($('#'+userSelfId).text());                $('#'+userSelfId).hide();                $( this ).data('act','like-icon');                var urlUnlike = BASE+"site/remove_like";                $.post(urlUnlike,                    {                        mid : '<?php echo $media_id; ?>'                    },                    function(data,status){                        console.log(data);                        if(status==false && data==false){                            alert("failed. Please try again!");                            $(this).removeClass('like-icon').addClass('like-icon-active');                            $('.likes li #'+userSelfId).show();                            $("#like-count").text(likeCount+1);                            $( this ).data('act','like-icon-active');                        }                        else{                            return true;                            $('.likes li #'+userSelfId).remove();                        }                    });            }        }    });    $("#view-comment").click(function(){        $( this ).html(loading);        $.get('<?php echo base_url()."site/get_comment?mid=$media_id"?>',function(data,status){            $("#view-comment").hide();            $(".comment ul").html(data);        });    });    $("#fb-share").click(function(){        window.open("https://www.facebook.com/sharer/sharer.php?u="+location.href, "Share instagram on Facebook","width=640,height=320");    });    $("#twitter-share").click(function(){        window.open("https://twitter.com/share?url="+location.href, "Share on Twitter","width=550,height=520");    });    function googlePlus(){        window.open('https://plus.google.com/share?url={'+location.href+'}','', 'menubar=no,toolbar=no,resizable=yes,scrollbars=yes,height=600,width=600');        return false;    }    var itemCaption = $(".item-caption").html();    var itemImg = $(".item-media img").attr("src");    function tumblrShare(){        //if photo        //var itemImg = $(".item-media img").attr("src");        var photo = encodeURIComponent(itemImg);        //var itemCaption = $(".item-caption").html();        var caption = encodeURIComponent(itemCaption);        var url = 'http://www.tumblr.com/share/photo?source='+photo+'&caption='+caption;        window.open(url, 'Share Instagram photo on Tumblr','menubar=no,toolbar=no ,resizable=yes,scrollbars=yes,height=500,width=700');        return false;    }    /* $(".item-media img").one("load", function() {     $(this).html('<img src="<?php echo img_url()?>animated_loading.gif ">');     // do stuff     //>     }).each(function() {     if(this.complete) {     //$(this).load();     $(this).fadeIn();     }     }); */    /*$('.item-media-mg img').bind("load", function() {     $('.item-media-mg').fadeIn();     });*/</script>