<script>	var tag = $(".more").attr("data-tag");	var maxId =  $(".more").attr("id");	var link =  '<?php echo ur(2) ?>';	var userSelfId = '<?php echo $user_self_id; ?>';		$(".more").click(function(){		$(this).hide();		getData();			 });		function getData(){			$("#loading").show();				if( link=='my_likes' ){			var url = BASE+"user/"+link+"?max_like_id="+maxId+"&more=1";		}else{			var url = BASE+"tag_more/?tag="+tag+"&max_id="+maxId+"&more=1";		}				$.get(url,function(data,status){			if (status=='success'){					$("#loading").hide();				$("#more"+maxId).remove();				$("#tagsul").append(data);				$("#tagsul").off('keydown');				$("#tagsul").on('keydown', '.commenttext', function(e) {					if ((e.which == 13) && !e.shiftKey) {						comment($(this));						return false;					}				});			}					else{				alert(status);			}		});	}			var loading = '<img src="'+BASE+'images/loading40x40.gif">';	var mediaId="";		function like(mediaId){				if(userSelfId==''){			alert("You should login");		}		else{			console.log(mediaId);						$('#like'+mediaId).removeClass('like-icon').addClass('like-icon-active').css('margin','3px 1px 0 0').attr('onclick','unlike("'+mediaId+'")');						var likeCount = $("#likecount"+mediaId);			var likeCountTotal =  likeCount.data('total');			console.log(likeCountTotal);				likeCount.text(likeCountTotal+1);											var url = BASE+"site/post_like";						$.post(url,			{				mid : mediaId						},			function(data,status){				console.log(data);											if(status==false){					alert("failed. Please try again!"); 					$('#like'+mediaId).removeClass('like-icon-active').addClass('like-icon').attr('onclick','like("'+mediaId+'")');					likeCount.text(likeCountTotal-1);				}					else{							return true;				}			});			}			}		function unlike(mediaId){				if(userSelfId==''){			alert("You should login");		}		else{			console.log(mediaId);						$('#like'+mediaId).removeClass('like-icon-active').addClass('like-icon').css('margin','0 1px').attr('onclick','like("'+mediaId+'")');						var likeCount = $("#likecount"+mediaId);			var likeCountTotal =  likeCount.data('total');			console.log(likeCountTotal);				likeCount.text(likeCountTotal-1);											var urlRemove = BASE+"site/remove_like";						$.post(urlRemove,			{				mid : mediaId						},			function(data,status){				console.log(data);											if(status==false){					alert("failed. Please try again!"); 					$('#like'+mediaId).removeClass('like-icon').addClass('like-icon-active').attr('onclick','unlike("'+mediaId+'")');					likeCount.text(likeCountTotal+1);				}					else{							return true;				}			});			}			}	$("#tagsul .img .tag-mg img").each(function(i) {		var Id = $(this).attr("id");		$(this).bind("load", function() {			$('#tag-mg'+Id).fadeIn();		});	});	</script>